import React, { useState } from 'react';
import axios from 'axios';

const NewApplicationForm = () => {
  const [file1, setFile1] = useState(null);
  const [file2, setFile2] = useState(null);
  const [file3, setFile3] = useState(null);
  const [application, setApplication] = useState({
    title: '',
    fullName: '',
    dateOfBirth: '',
    gender: '',
    emailAddress: '',
    phoneNumber: '',
    idNumber: '',
    stateId: '',
    operationAreaId: '',
    branchId: '',
    territoryId: '',
    subTerritoryId: '',
    streetAddress: '',
    plotNumber: '',
    nearestLandMark: '',
    customerType: '',
    billDeliveryMethod: '',
    customerCategory: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setApplication({ ...application, [name]: value });
  };

  const handleFile1Change = (e) => {
    setFile1(e.target.files[0]);
  };

  const handleFile2Change = (e) => {
    setFile2(e.target.files[0]);
  };

  const handleFile3Change = (e) => {
    setFile3(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', file1);
    formData.append('file', file2);
    formData.append('file', file3);

    formData.append('application', JSON.stringify(application));

    try {
      const response = await axios.post('http://localhost:5102/NewApplication', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert(response.data); // Show success message
    } catch (error) {
      alert('Error submitting application'); // Show error message
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title:</label>
        <input type="text" name="title" value={application.title} onChange={handleChange} />
      </div>
      <div>
        <label>Full Name:</label>
        <input type="text" name="fullName" value={application.fullName} onChange={handleChange} />
      </div>
      <div>
        <label>Date of Birth:</label>
        <input type="text" name="dateOfBirth" value={application.dateOfBirth} onChange={handleChange} />
      </div>
      {/* Add more fields as needed */}
      <div>
        <label>Phone Number:</label>
        <input type="text" name="phoneNumber" value={application.phoneNumber} onChange={handleChange} />
      </div>
      <div>
        <label>File 1:</label>
        <input type="file" onChange={handleFile1Change} />
      </div>
      <div>
        <label>File 2:</label>
        <input type="file" onChange={handleFile2Change} />
      </div>
      <div>
        <label>File 3:</label>
        <input type="file" onChange={handleFile3Change} />
      </div>
      <button type="submit">Submit Application</button>
    </form>
  );
};

export default NewApplicationForm;
